import logo from './logo.svg';
import './App.css';
import { Button } from 'react-bootstrap';
import NavBar from './components/Navbar';
import { Navigate, Route, Routes } from 'react-router-dom';
import MissingPage from './pages/MissingPage';
import Login from './pages/Login';
import Signup from './pages/Signup';
import { useEffect, useState } from 'react';
import Home from './pages/Home';
import SpecificCard from './pages/SpecificCard';

function App() {



  return ( 
    <>
      <Routes>
        <Route path = '/' >
          <Route index element={<Login />} />
          <Route path = 'signup' element = {<Signup />} />
          {/* <Route path = 'home' element = {localStorage.getItem("token") ? <Home /> : <Navigate to='/' />} />  */}
          <Route path = 'home' element = {<Home />} /> 
          <Route path = 'home/surah' element = {<Home />} /> 


          <Route path = 'home/surah' element = {localStorage.getItem("token")? <SpecificCard /> : <Navigate to ='/' />} /> 
        </Route>
        {/* <Route path='/home' >
          <Route index element = {localStorage.getItem("token") ? <Home /> : <Navigate to='/' />} /> 
          <Route path = 'surah' element = {localStorage.getItem("token")? <SpecificCard /> : <Navigate to ='/' />} /> 
        </Route> */}


        <Route path="*" element={<MissingPage />} />
      </Routes>
    </>

  );
}

export default App;
