import logo from './logo.svg';
import './App.css';
import { Button } from 'react-bootstrap';
import NavBar from './components/Navbar';
import { Navigate, Route, Routes } from 'react-router-dom';
import MissingPage from './pages/MissingPage';
import Login from './pages/Login';
import Signup from './pages/Signup';
import { useEffect, useState } from 'react';
import Home from './pages/Home';
import SpecificCard from './pages/SpecificCard';

function App() {

  const [user, setUser] = useState(null); // set user session here


  useEffect(()=>{

    const token = localStorage.get

    localStorage.getItem("token") ? setUser(localStorage.getItem("token")): setUser(false);
  }, [localStorage.getItem("token")])

  return ( 
    <>
      <Routes>
        <Route path = '/' >
          <Route index element={<Login />} />
          <Route path = '/signup' element = {<Signup />} />
        </Route>
        <Route path='/home' >
          <Route index element = {user? <Home /> : <Navigate to='/' />} /> 
          <Route path = '/home/surah' element = {user? <SpecificCard /> : <Navigate to ='/' />} /> 
        </Route>


        <Route path="*" element={<MissingPage />} />
      </Routes>
    </>

  );
}

export default App;
